package com.example.users_sp_da

import android.content.Context
import android.content.DialogInterface
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.util.Log
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.users_sp_da.databinding.ActivityMainBinding
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.textfield.TextInputEditText

class MainActivity : AppCompatActivity(), OnClickListener {

    private lateinit var userAdapter: UserAdapter
    private lateinit var linearLayoutManager: RecyclerView.LayoutManager

    private lateinit var binding: ActivityMainBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

        val preferences = getPreferences(Context.MODE_PRIVATE)

        val isFirstTime = preferences.getBoolean(getString(R.string.sp_first_time),true)
        Log.i("SP","${getString(R.string.sp_first_time)} = $isFirstTime")
        Log.i("SP","${getString(R.string.sp_username)} = ${preferences.getString(getString(R.string.sp_username),"NA")}")

        if(isFirstTime) {
            val dialogView = layoutInflater.inflate(R.layout.dialog_register,null)
            MaterialAlertDialogBuilder(this)
                .setTitle(R.string.dialog_title)
                .setView(dialogView)
                .setCancelable(false)
                .setPositiveButton(R.string.dialog_confirm, DialogInterface.OnClickListener { dialogInterface, i ->
                    val username = dialogView.findViewById<TextInputEditText>(R.id.etUsername)
                        .text.toString()
                    with(preferences.edit()){
                        putBoolean(getString(R.string.sp_first_time), false).commit()
                        putString(R.string.sp_username, username)
                            .apply()
                    }
                })
                .show()
        }

        userAdapter = UserAdapter(getUsers(), this)
        linearLayoutManager = LinearLayoutManager(this)

        binding.recyclerView.apply{
            setHasFixedSize(true)
            layoutManager = linearLayoutManager
            adapter = userAdapter

        }
    }

    private  fun getUsers(): MutableList<User>{
        val users = mutableListOf<User>()

        val josue = User(1, "Josue", "Mu√±oz","data:image/jpeg;base64,")
        val luis = User(2, "Luis", "Perez", "data:image/jpeg;base64,")
        val javier = User(3, "Javier", "Gomez", "data:image/jpeg;base64,")
        val emma =  User(4, "Emma", "Cruz", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT7wqH1_R9gj9cvaQwi5sGo70WkEIUuF9JoUQ&usqp=CAU")


        users.add(josue)
        users.add(luis)
        users.add(javier)
        users.add(emma)
        users.add(josue)
        users.add(luis)
        users.add(javier)
        users.add(emma)
        users.add(josue)
        users.add(luis)
        users.add(javier)
        users.add(emma)

        return users
    }

    override fun onClick(user: User, position: Int) {
        Toast.makeText(this, "$position: ${user.getFullName()}", Toast.LENGTH_SHORT).show()
    }
}